answer_code:
const SLIDE_TO_SHOW = 3;
const WIDTH = 320;
const HEIGHT = 180;
const DURATION = 9;
const data = [1, 2, 3, 4, 5, 6];
const tl = new TimelineMax({
  repeat: -1,
});

class App extends React.Component {
  componentDidMount() {
    const { content } = this.refs;
    tl.add(TweenLite.to(content, DURATION, {
      transform: `translate3d(${data.length * WIDTH * -1}px, 0, 0)`, 
      ease: Linear.easeNone, 
    }));
    tl.add(TweenLite.to(content, 0, { 
      transform: `translate3d(0, 0, 0)`
    }));
  }
  render() {
    return (
      <div 
        className="marquee"
        style={{
          width: `${SLIDE_TO_SHOW * WIDTH}px`,
          height: `${HEIGHT}px`,
        }}
      >
        <div
          className="child"
          style={{
            width: `${(data.length + SLIDE_TO_SHOW) * WIDTH}px`,
          }}
          ref="content"
          onMouseEnter={() => {
            tl.pause();
          }}
          onMouseLeave={() => {
            tl.play();
          }}
        >
          {
            data.map((item, index) => {
              return (
                <img 
                  src={`http://via.placeholder.com/320x180?text=${index+1}`}
                  key={index+1}
                />
              );
            })
          }
          {
            data.map((item, index) => {
              if (index + 1 <= SLIDE_TO_SHOW) {
                return (
                  <img 
                    src={`http://via.placeholder.com/320x180?text=${index+1}`}
                    key={`last-${index + 1}`}
                  />
                ); 
              }
              return null;
            })
          }
        </div>
      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('root'));

