const announcementDetails = [
  {id: 7, title: "welcome to london"},
  {id: 6, title: "welcome to Italy"},
  {id: 5, title: "welcome to Paris"},
  {id: 4, title: "Welcome to France"}
];

function Marquee({announcements}){
  const marqueeRef = createRef();
  const [stopped, toggleStopped] = useState(false);

  useEffect(()=>{
    if(stopped) marqueeRef.stop();
    else marqueeRef.start();
  }, [stopped]);
  return <marquee
    ref={marqueeRef}
    onMouseOut={toggleStopped(false)}
    onMouseOver={toggleStopped(true)}
    id='scroll_news'
